  <div class="sidebar" :class="{'active': $store.state.appStore.isMenuOpen}">
    <ul class="main-nav">
      <li v-for="(nav, i) in filteredNav" :key="`nav-${i}`">
        <router-link :to="nav.url" v-if="!nav.action" @click.native="hideMenu">
          <span class="icon" v-html="nav.icon"></span>
          <span>{{ nav.name }}</span>
        </router-link>

        <a href="#!" v-else @click.prevent="handleClick(nav.action)">
          <span class="icon" v-html="nav.icon"></span>
          <span>{{ nav.name }}</span>
        </a>
      </li>
    </ul>
