        { name: 'Bezoekersreg',            url: '/visitors',           icon: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6"><path stroke-linecap="round" stroke-linejoin="round" d="M12 6.042A8.967 8.967 0 0 0 6 3.75c-1.052 0-2.062.18-3 .512v14.25A8.987 8.987 0 0 1 6 18c2.305 0 4.408.867 6 2.292m0-14.25a8.966 8.966 0 0 1 6-2.292c1.052 0 2.062.18 3 .512v14.25A8.987 8.987 0 0 0 18 18a8.967 8.967 0 0 0-6 2.292m0-14.25v14.25" /></svg>' },
        { name: 'Yuki',                    url: '/yuki',               icon: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="size-6"><path stroke-linecap="round" stroke-linejoin="round" d="M12 6v12m-3-2.818.879.659c1.171.879 3.07.879 4.242 0 1.172-.879 1.172-2.303 0-3.182C13.536 12.219 12.768 12 12 12c-.725 0-1.45-.22-2.003-.659-1.106-.879-1.106-2.303 0-3.182s2.9-.879 4.006 0l.415.33M21 12a9 9 0 1 1-18 0 9 9 0 0 1 18 0Z" /></svg>' },
        { name: 'Toegangscodes',           url: '/gatecontrols',       icon: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6"><path stroke-linecap="round" stroke-linejoin="round" d="M15.75 9V5.25A2.25 2.25 0 0013.5 3h-6a2.25 2.25 0 00-2.25 2.25v13.5A2.25 2.25 0 007.5 21h6a2.25 2.25 0 002.25-2.25V15m3 0l3-3m0 0l-3-3m3 3H9"</svg>' },
        { name: 'Overzicht leveringen',    url: '/delivery/overview',  icon: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-6 h-6"><path strokeLinecap="round" strokeLinejoin="round" d="M8.25 18.75a1.5 1.5 0 01-3 0m3 0a1.5 1.5 0 00-3 0m3 0h6m-9 0H3.375a1.125 1.125 0 01-1.125-1.125V14.25m17.25 4.5a1.5 1.5 0 01-3 0m3 0a1.5 1.5 0 00-3 0m3 0h1.125c.621 0 1.129-.504 1.09-1.124a17.902 17.902 0 00-3.213-9.193 2.056 2.056 0 00-1.58-.86H14.25M16.5 18.75h-2.25m0-11.177v-.958c0-.568-.422-1.048-.987-1.106a48.554 48.554 0 00-10.026 0 1.106 1.106 0 00-.987 1.106v7.635m12-6.677v6.677m0 4.5v-4.5m0 0h-12" /></svg>' },
        { name: 'Historiek loten',         url: '/history',            icon: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6"><path stroke-linecap="round" stroke-linejoin="round" d="M9 12h3.75M9 15h3.75M9 18h3.75m3 .75H18a2.25 2.25 0 002.25-2.25V6.108c0-1.135-.845-2.098-1.976-2.192a48.424 48.424 0 00-1.123-.08m-5.801 0c-.065.21-.1.433-.1.664 0 .414.336.75.75.75h4.5a.75.75 0 00.75-.75 2.25 2.25 0 00-.1-.664m-5.8 0A2.251 2.251 0 0113.5 2.25H15c1.012 0 1.867.668 2.15 1.586m-5.8 0c-.376.023-.75.05-1.124.08C9.095 4.01 8.25 4.973 8.25 6.108V8.25m0 0H4.875c-.621 0-1.125.504-1.125 1.125v11.25c0 .621.504 1.125 1.125 1.125h9.75c.621 0 1.125-.504 1.125-1.125V9.375c0-.621-.504-1.125-1.125-1.125H8.25zM6.75 12h.008v.008H6.75V12zm0 3h.008v.008H6.75V15zm0 3h.008v.008H6.75V18z" /></svg>' },
        { name: 'Aankoopborderels',        url: '/borderels',          icon: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6"><path stroke-linecap="round" stroke-linejoin="round" d="M11.35 3.836c-.065.21-.1.433-.1.664 0 .414.336.75.75.75h4.5a.75.75 0 00.75-.75 2.25 2.25 0 00-.1-.664m-5.8 0A2.251 2.251 0 0113.5 2.25H15c1.012 0 1.867.668 2.15 1.586m-5.8 0c-.376.023-.75.05-1.124.08C9.095 4.01 8.25 4.973 8.25 6.108V8.25m8.9-4.414c.376.023.75.05 1.124.08 1.131.094 1.976 1.057 1.976 2.192V16.5A2.25 2.25 0 0118 18.75h-2.25m-7.5-10.5H4.875c-.621 0-1.125.504-1.125 1.125v11.25c0 .621.504 1.125 1.125 1.125h9.75c.621 0 1.125-.504 1.125-1.125V18.75m-7.5-10.5h6.375c.621 0 1.125.504 1.125 1.125v9.375m-8.25-3l1.5 1.5 3-3.75" /></svg>' },
        { name: 'Logout',                  url: '/logout',             icon: '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6"> <path stroke-linecap="round" stroke-linejoin="round" d="M15.75 9V5.25A2.25 2.25 0 0013.5 3h-6a2.25 2.25 0 00-2.25 2.25v13.5A2.25 2.25 0 007.5 21h6a2.25 2.25 0 002.25-2.25V15M12 9l-3 3m0 0l3 3m-3-3h12.75" /> </svg>\n', action: 'logout' },
      ]
    }
  },
  methods: {
    handleClick(action) {
      if(action === 'logout') {
        this.hideMenu();
        this.$store.dispatch(LOGOUT_USER);
      }
    },
    hideMenu() {
      this.$store.dispatch('appStore/' + IS_MENU_OPEN, false);
    },
  },
  computed: {
    filteredNav() {
      if(this.$store.getters.getUser && this.$store.getters.getUser.role === 'Admin') {
        return this.navigation;
      } else if(this.$store.getters.getUser && this.$store.getters.getUser.role === 'Visitor'){
        let $filtered_nav = this.navigation.filter((e) => e.url !== "/users");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/gatecontrols");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/employees");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/delivery/overview");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/history");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/borderels");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/yuki");

        return $filtered_nav
      }else if(this.$store.getters.getUser && this.$store.getters.getUser.role === 'DeliveryAdmin') {
        let $filtered_nav = this.navigation.filter((e) => e.url !== "/users");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/gatecontrols");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/employees");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/visitors");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/history");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/borderels");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/yuki");

        return $filtered_nav
      }
      else {
        let $filtered_nav = this.navigation.filter((e) => e.url !== "/users");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/gatecontrols");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/employees");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/visitors");
        $filtered_nav = $filtered_nav.filter((e) => e.url !== "/yuki");

        return $filtered_nav
      }
    },
  },
}
</script>
